import { Component } from 'react';

class ContactList extends Component {
  state = {
    contacts: [],
    name: '',
  };

  render() {
    return (
      <div>
        <h1>PhoneBook</h1>
        <label>Name</label>
        <br />
        <input
          type="text"
          onChange={e => {
            console.log(e.target.value);
          }}
          name="name"
          pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
          title="Name may cont ain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
          required
        />{' '}
        <br />
        <button>Add contact</button> <br />
        <h2>Contacts:</h2>
      </div>
    );
  }
}

export default ContactList;
